import React from 'react';
import '../css/HInfo.css';
// Import the icons you mentioned
import icon1 from '../components/icon1.png'; 
import icon2 from '../components/icon2.png';
// ... import other icons as needed

const HInfo = ({ hospital }) => {
    return (
        <div className="hospital-details-container">
            <h1 className="hospital-name">{hospital.name}</h1>

            <div className="hospital-info-section">
                <img src={icon1} alt="Icon 1" className="info-icon"/>
                <div className="info-content">
                    <h3>Info Title 1</h3>
                    <p>{hospital.info1}</p>
                </div>
            </div>

            <div className="hospital-info-section">
                <img src={icon2} alt="Icon 2" className="info-icon"/>
                <div className="info-content">
                    <h3>Info Title 2</h3>
                    <p>{hospital.info2}</p>
                </div>
            </div>

            {/* Add more info sections as per the image */}
        </div>
    );
}

export default HInfo;
