/* global kakao */
import React, { useEffect } from "react";
import "../components/greensign.png"
import "../components/orangesign.png"
import "../components/redsign.png"
import "../components/yellowsign.png"

const sampleHospitals = [
    {
      id: 1,
      name: "Hospital 1",
      lat: 33.450701,
      lng: 126.570667,
      Acongestion: 2
    },
    {
      id: 2,
      name: "Hospital 2",
      lat: 33.451701,
      lng: 126.571667,
      Acongestion: 5
    },
    {
      id: 3,
      name: "Hospital 3",
      lat: 33.452701,
      lng: 126.572667,
      Acongestion: 4
    },
    // ... 여기에 추가 병원 데이터를 넣을 수 있습니다.
  ];

const MapOrder = () => {
    useEffect(() => {
        // 지도를 표시할 div
        var mapContainer = document.getElementById('map');
        
        // 지도의 중심좌표와 확대 레벨
        var mapOption = {
            center: new kakao.maps.LatLng(33.450701, 126.570667),
            level: 3
        };

        // 지도를 생성합니다
        var map = new kakao.maps.Map(mapContainer, mapOption);
        
        // 병원 데이터를 기반으로 지도에 아이콘을 표시하는 함수
        const displayHospitalsOnMap = (hospitals) => {
            hospitals.forEach(hospital => {
                const position = new kakao.maps.LatLng(hospital.lat, hospital.lng); // 병원의 위도, 경도

                // 혼잡도에 따른 아이콘 선택 (가상의 아이콘 URL을 사용합니다)
                let iconSrc;
                if (hospital.Acongestion >= 20) {
                    iconSrc = 'redsign.png';
                } else if (hospital.Acongestion >= 10) {
                    iconSrc = 'orangesign.png';
                } else if (hospital.Acongestion >= 5) {
                    iconSrc = 'yellow.png';
                } else if (hospital.Acongestion >= 0) {
                    iconSrc = 'green.png';
                }

                const imageSize = new kakao.maps.Size(24, 24);
                const imageOption = { offset: new kakao.maps.Point(27, 69) };
                
                const markerImage = new kakao.maps.MarkerImage(iconSrc, imageSize, imageOption);
                const marker = new kakao.maps.Marker({
                    position: position,
                    image: markerImage
                });
                
                marker.setMap(map);
            });
        };

        // 제공된 병원 데이터를 기반으로 지도에 아이콘을 표시
        displayHospitalsOnMap(sampleHospitals);

    }, []);

    return (
        <div id="map" style={{ width: '100%', height: '500px' }}></div>
    );
}

export default MapOrder;
