        <div
          id="map"
          style={{
            width: "100%",
            height: "500px",
            borderRadius: "15px",
            boxShadow: "0 5px 15px rgba(0, 0, 0, 0.2)",
            margin: "20px 0",
            border: "1px solid #ddd"
          }}
        ></div>
      </div>
    );
  };
  
  export default MapOrder;
