import React, { useState } from 'react';
import './CongestionLevelOrder.css'; // CSS 파일 import

const sampleData = {
  adult: [
    { id: 1, name: 'Location A', congestion: 'High' },
    { id: 2, name: 'Location B', congestion: 'Medium' },
    // ... 추가 데이터
  ],
  child: [
    { id: 3, name: 'Location C', congestion: 'Low' },
    { id: 4, name: 'Location D', congestion: 'High' },
    // ... 추가 데이터
  ],
};

const CongestionLevelOrder = () => {
  const [currentList, setCurrentList] = useState('adult'); // 기본으로 성인 리스트 표시

  return (
    <div className="congestion-container">
      <div className="button-group">
        <button onClick={() => setCurrentList('adult')}>성인</button>
        <button onClick={() => setCurrentList('child')}>소아</button>
      </div>

      <ul className="list">
        {sampleData[currentList].map((item) => (
          <li key={item.id}>
            {item.name} - {item.congestion}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CongestionLevelOrder;
