    "응급내시경",
    "응급투석",
    "조산산모",
    "신생아",
    "중증화상",
  ],
  Available_Beds: [
    "응급실성인병상",
    "응급실소아병상",
    "수술실",
    "신경과중환자실",
    "신생아중환자실",
    "흉부외과중환자실",
    "일반중환자실",
