import React, { useState, useEffect } from "react";
import "../css/CongestionLevelOrder.css";
import axios from "axios";

const sampleData = [
  { id: 1, name: "Location A", Acongestion: "10", Bcongestion: "10" },
  { id: 2, name: "Location B", Acongestion: "9", Bcongestion: "6" },
  { id: 3, name: "Location C", Acongestion: "8", Bcongestion: "7" },
  { id: 4, name: "Location D", Acongestion: "6", Bcongestion: "1" },
  { id: 5, name: "Location E", Acongestion: "5", Bcongestion: "4" },
  { id: 6, name: "Location F", Acongestion: "2", Bcongestion: "5" },
  { id: 7, name: "Location D", Acongestion: "12", Bcongestion: "11" },
  { id: 8, name: "Location E", Acongestion: "53", Bcongestion: "41" },
  { id: 9, name: "Location F", Acongestion: "21", Bcongestion: "51" },
  { id: 10, name: "Location D", Acongestion: "63", Bcongestion: "12" },
  { id: 11, name: "Location E", Acongestion: "54", Bcongestion: "43" },
  { id: 12, name: "Location F", Acongestion: "25", Bcongestion: "53" },
];

const CongestionLevelOrder = () => {
  const [sortedData, setSortedData] = useState([...sampleData]);
  const [activeButton, setActiveButton] = useState("");
  const [firstAddress, setFirstAddress] = useState("");
  const [secondAddress, setSecondAddress] = useState("");

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const latitude = position.coords.latitude;
          const longitude = position.coords.longitude;
          try {
            const response = await fetch(
              `https://dapi.kakao.com/v2/local/geo/coord2address.json?x=${longitude}&y=${latitude}`,
              {
                headers: {
                  Authorization: "KakaoAK e66ae55f0df11d36b2b954ae39f001eb",
                  "Content-Type":
                    "application/x-www-form-urlencoded;charset=utf-8",
                },
              }
            );
            const data = await response.json();
            if (data.documents && data.documents.length > 0) {
              const address = data.documents[0].address;
              const splitAddress = address.address_name.split(" ");
              setFirstAddress(splitAddress[0]);
              setSecondAddress(splitAddress[1]);
            }
          } catch (error) {
            console.error("Error fetching address", error);
          }
        },
        (error) => {
          console.error("Error getting geolocation", error);
        }
      );
    }
  }, []);

  const handleSort = async (type) => {
    if (type === "adult") {
      setActiveButton("adult");
    } else {
      setActiveButton("child");
    }

    // 주석 처리된 백엔드 API 요청 코드
    /*
    try {
      const response = await axios.post("YOUR_BACKEND_API_URL", {
        Firstaddress: firstAddress,
        secondaddress: secondAddress,
        isadult: type === "adult",
      });

      if (response.data) {
        // 백엔드에서 받은 병원 데이터로 상태 업데이트
        setSortedData(response.data);
      }
    } catch (error) {
      console.error("Error fetching sorted hospitals", error);
    }
    */
  };

  return (
    <div className="CongestionLevelOrder-container">
      <h2 className="CongestionLevelOrder-h2">혼잡도 기준</h2>
      <div className="CongestionLevelOrder-button-group">
        <button
          onClick={() => handleSort("adult")}
          className={activeButton === "adult" ? "active" : ""}
        >
          성인 혼잡도
        </button>
        <button
          onClick={() => handleSort("child")}
          className={activeButton === "child" ? "active" : ""}
        >
          소아 혼잡도
        </button>
      </div>

      <ul className="CongestionLevelOrder-list">
        {sortedData.map((item) => (
          <li key={item.id} className="CongestionLevelOrder-item">
            <span className="CongestionLevelOrder-name">{item.name}</span>
            <span className="CongestionLevelOrder-congestion-info">
              성인: {item.Acongestion}
            </span>
            <span className="CongestionLevelOrder-congestion-info">
              소아: {item.Bcongestion}
            </span>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CongestionLevelOrder;
