import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../css/Login.css'; 
import axios from 'axios'; // axios를 import 합니다.

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  
  const handleLogin = async () => {
    try {
      // 아래의 주소는 백엔드 API 주소로 변경해야 합니다.
      const response = await axios.post('YOUR_BACKEND_API_ENDPOINT', { 
        email: email,
        password: password
      });

      if (response.data.success) {
        console.log("Logged in successfully!");
        // Todo : 넘어가는 로직 추가하기
      } else {
        setError(response.data.message || "Login failed");
      }
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={handleLogin}>Login</button>
      <Link to="/Regist"><button>Regist</button></Link>
      <Link to="/Main"><button>Main</button></Link> 
      {error && <p>{error}</p>}
    </div>
  );
};

export default Login;
