import React, { useState } from "react";
import axios from "axios"; 
import "../css/Regist.css";

const Regist = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [error, setError] = useState("");

  const handleRegister = async () => {
    if (password !== confirmPassword) {
      setError("Passwords do not match");
      return;
    }

    try {
      // 아래의 주소는 백엔드 API 주소로 변경해야 합니다.
      const response = await axios.post("YOUR_BACKEND_REGISTRATION_ENDPOINT", {
        name: name,
        email: email,
        password: password,
      });

      if (response.data.success) {
        console.log("Registration successful!");
        // Optionally: Redirect to login or display success message
      } else {
        setError(response.data.message || "Registration failed");
      }
    } catch (err) {
      setError(err.message || "An error occurred");
    }
  };

  return (
    <div className="regist-container">
      <h2>Regist</h2>
      <input
        type="text"
        placeholder="Name"
        value={name}
        onChange={(e) => setName(e.target.value)}
        className="regist-input"
      />
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        className="regist-input"
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        className="regist-input"
      />
      <input
        type="password"
        placeholder="Confirm Password"
        value={confirmPassword}
        onChange={(e) => setConfirmPassword(e.target.value)}
        className="regist-input"
      />
      <button onClick={handleRegister} className="regist-button">
        Register
      </button>
      {error && <p className="regist-error">{error}</p>}
    </div>
  );
};

export default Regist;
