  },
  {
    id: 23,
    name: "Hospital 23",
    lat: 33.458701,
    lng: 126.572867,
    Acongestion: 4,
    Pcongestion: 19,
  },
];

const MapOrder = () => {
  const [congestionType, setCongestionType] = useState("Acongestion"); // 기본값은 성인 혼잡도

  useEffect(() => {
    // 지도를 표시할 div
    var mapContainer = document.getElementById("map");

    // 지도의 중심좌표와 확대 레벨
    var mapOption = {
      center: new kakao.maps.LatLng(33.450701, 126.570667),
      level: 7,
    };

    // 지도를 생성합니다
    var map = new kakao.maps.Map(mapContainer, mapOption);

    // 지도의 줌 아웃 비율에 따라 아이콘 크기를 조절하는 함수
    kakao.maps.event.addListener(map, "zoom_changed", function () {
      const zoomLevel = map.getLevel();
      const size = new kakao.maps.Size(40 - zoomLevel * 2, 40 - zoomLevel * 2);
      // ... 여기에 아이콘 크기 조절 로직을 추가합니다.
    });

    // 병원 데이터를 기반으로 지도에 아이콘을 표시하는 함수
    const displayHospitalsOnMap = (hospitals) => {
      hospitals.forEach((hospital) => {
        const position = new kakao.maps.LatLng(hospital.lat, hospital.lng); // 병원의 위도, 경도

        let iconSrc;
        const congestionValue = hospital[congestionType];
        if (congestionValue >= 20) {
          iconSrc = redSign;
        } else if (congestionValue >= 10) {
          iconSrc = orangeSign;
        } else if (congestionValue >= 5) {
          iconSrc = yellowSign;
        } else if (congestionValue >= 0) {
          iconSrc = greenSign;
        }

        const imageSize = new kakao.maps.Size(40, 40);
        const imageOption = { offset: new kakao.maps.Point(27, 69) };

        const markerImage = new kakao.maps.MarkerImage(
          iconSrc,
          imageSize,
          imageOption
        );
        const marker = new kakao.maps.Marker({
          position: position,
          image: markerImage,
        });

        marker.setMap(map);
      });
    };

    displayHospitalsOnMap(sampleHospitals);
  }, [congestionType]);

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '20px' }}>
        <button 
          onClick={() => setCongestionType("Acongestion")}
          style={{
            padding: '10px 20px',
            marginRight: '10px',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
            backgroundColor: congestionType === 'Acongestion' ? '#007BFF' : '#ddd',
            color: congestionType === 'Acongestion' ? 'white' : 'black',
          }}
        >
          성인 혼잡도 기준
        </button>
        <button 
          onClick={() => setCongestionType("Pcongestion")}
          style={{
            padding: '10px 20px',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
            backgroundColor: congestionType === 'Pcongestion' ? '#007BFF' : '#ddd',
            color: congestionType === 'Pcongestion' ? 'white' : 'black',
          }}
        >
          소아 혼잡도 기준
        </button>
      </div>
      <div
        id="map"
        style={{
          width: "100%",
          height: "500px",
          borderRadius: "15px",
          boxShadow: "0 5px 15px rgba(0, 0, 0, 0.2)",
          margin: "20px 0",
          border: "1px solid #ddd",
        }}
      ></div>
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', flexDirection: 'column' }}>
        <img src="/src/components/4sign.png" alt="지도 기준" />
        <span>아이콘 설명</span>
      </div>
    </div>
  );
};

export default MapOrder;
